    let historyManager = TypingHistoryManager()
    var lastSyncedContext: String = ""
    var currentSmartActions: [[String: Any]] = []
    
    deinit {
        suggestionTimer?.invalidate()
        suggestionTimer = nil
        suggestionLabel = nil
        audioRecorder?.stop()
        audioRecorder = nil
    }

    override func viewDidLoad() {
        super.viewDidLoad()
        setupKeyboardLayout()
        
        // Link real-time thought updates
        historyManager.onThoughtUpdate = { [weak self] message in
            DispatchQueue.main.async {
                self?.suggestionLabel?.text = message
                if message.hasPrefix("ğŸ’¡") {
                    self?.suggestionLabel?.textColor = UIColor(red: 0.1, green: 0.64, blue: 0.38, alpha: 1.0) // Google Green
                } else {
                    self?.suggestionLabel?.textColor = UIColor(red: 0.1, green: 0.45, blue: 0.91, alpha: 1.0) // Google Blue
                }
            }
        }
        historyManager.onActionsReceived = { [weak self] actions in
            DispatchQueue.main.async {
                self?.renderActionChips(actions: actions)
            }
        }
        historyManager.onResultReceived = { [weak self] result in
            DispatchQueue.main.async {
                self?.lastSuggestedCompletion = result
                self?.suggestionLabel?.text = "\(result) (Tap to insert)"
                self?.suggestionLabel?.textColor = .black
                self?.suggestionLabel?.backgroundColor = UIColor(red: 0.95, green: 0.98, blue: 1.0, alpha: 1.0) // Soft highlight
            }
        }
    }
    
    func renderActionChips(actions: [[String: Any]]) {
        guard let stack = self.smartActionStack else { return }
        self.currentSmartActions = actions // Cache for lookup
        
        // Clear children
        stack.arrangedSubviews.forEach { $0.removeFromSuperview() }
        
        if actions.isEmpty {
            if let status = self.smartStatusLabel {
                stack.addArrangedSubview(status)
            }
            return
        }
        
        for action in actions {
            guard let label = action["label"] as? String,
                  let id = action["id"] as? String else { continue }
            
            let btn = createChip(title: label, actionId: id)
            stack.addArrangedSubview(btn)
        }
    }
    
    override func viewDidAppear(_ animated: Bool) {
        super.viewDidAppear(animated)
        // Reset state so we can detect focus on new fields
        lastSyncedContext = ""
    }
    
    func showView(_ viewType: ActiveView) {
        activeView = viewType
        
        qwertyRowsStack?.isHidden = (viewType != .main)
        emojiView?.isHidden = true 
        
        if viewType == .main {
            updateEmojiButtonTitle(isEmoji: false)
            isEmojiView = false
        }
        
        UIView.transition(with: self.view, duration: 0.2, options: .transitionCrossDissolve, animations: nil)
    }

    private func updateEmojiButtonTitle(isEmoji: Bool) {
        UIView.performWithoutAnimation {
            guard let btn = self.emojiButton else { return }
            
            if isEmoji {
                // Switch to ABC state (Text mode)
                btn.setImage(nil, for: .normal)
                btn.setTitle("ABC", for: .normal)
                btn.titleLabel?.font = UIFont.systemFont(ofSize: 16, weight: .semibold)
            } else {
                // Switch to Emoji state (Icon mode)
                // 1. Try Custom Image
                if let image = UIImage(named: "ic_native_emoji") ?? UIImage(named: "ic_native_emoji.png") {
                    btn.setImage(image.withRenderingMode(.alwaysOriginal), for: .normal)
                    btn.imageView?.contentMode = .scaleAspectFit
                    btn.setTitle(nil, for: .normal)
                } 
                // 2. Fallback to Grinning Face
                else {
                    // Use Text Variation Selector (\u{FE0E}) to force monochrome
                    btn.setImage(nil, for: .normal)
                    btn.setTitle("ğŸ˜€\u{FE0E}", for: .normal)
                    btn.titleLabel?.font = UIFont.systemFont(ofSize: 26) 
                }
            }
            btn.titleLabel?.adjustsFontSizeToFitWidth = true
            btn.titleLabel?.minimumScaleFactor = 0.5
            btn.layoutIfNeeded()
        }
    }

    @objc func toggleEmojiView() {
        if activeView != .main { showView(.main) }
        
        isEmojiView = !isEmojiView
        if isEmojiView {
            qwertyRowsStack?.isHidden = true
            bottomKeyRow?.isHidden = true
            emojiView?.isHidden = false
            updateEmojiButtonTitle(isEmoji: true)
        } else {
            qwertyRowsStack?.isHidden = false
            bottomKeyRow?.isHidden = false
            emojiView?.isHidden = true
            updateEmojiButtonTitle(isEmoji: false)
        }
        self.view.layoutIfNeeded()
    }
}

